<?php// *************************************************************************//// Project      : jSchuetze for Joomla                                      //// @package     : com_jschuetze                                             //// @file        : /admin/controllers/awards.php                             //// @implements  : Class jSchuetzeControllerAwards                           //// @description : Controller for editing the awards-List                    //// Version      : 1.1.3                                                     //// *************************************************************************//// No direct access.defined('_JEXEC') or die;jimport('joomla.application.component.controlleradmin');// Eventlist controller class.class jSchuetzeControllerAwards extends JControllerAdmin{	// constructor (registers additional tasks to methods)	// @return void	function __construct()	{		parent::__construct();    }    	public function getModel($name = 'award', $prefix = 'jSchuetzeModel', 							 $config = array('ignore_request' => true))	{		$model = parent::getModel($name, $prefix, $config);		return $model;	}	      public function pfand_publish()    {        $this->setRedirect( 'index.php?option=com_jschuetze&view=awards' );        $cid   = JRequest::getVar( 'cid', array(), 'post', 'array' );               $this->getModel()->changeDBField('pfand', '1', $cid);    }        public function pfand_unpublish()    {        $this->setRedirect( 'index.php?option=com_jschuetze&view=awards' );        $cid   = JRequest::getVar( 'cid', array(), 'post', 'array' );               $this->getModel()->changeDBField('pfand', '0', $cid);    }        public function memberfiles_publish()    {        $this->setRedirect( 'index.php?option=com_jschuetze&view=awards' );        $cid   = JRequest::getVar( 'cid', array(), 'post', 'array' );               $this->getModel()->changeDBField('memberfiles', '1', $cid);    }        public function memberfiles_unpublish()    {        $this->setRedirect( 'index.php?option=com_jschuetze&view=awards' );        $cid   = JRequest::getVar( 'cid', array(), 'post', 'array' );               $this->getModel()->changeDBField('memberfiles', '0', $cid);    }}