<?php// *************************************************************************//// Project      : jSchuetze for Joomla                                      //// @package     : com_jschuetze                                             //// @file        : /admin/controllers/members.php                            //// @implements  : Class jSchuetzeControllerMembers                          //// @description : Controller for editing the Member-List                    //// Version      : 1.0.0                                                     //// *************************************************************************//// No direct access.defined('_JEXEC') or die;jimport('joomla.application.component.controlleradmin');// Eventlist controller class.class jSchuetzeControllerMembers extends JControllerAdmin{	// constructor (registers additional tasks to methods)	// @return void	function __construct()	{		parent::__construct();    }    	/**	 * Proxy for getModel.	 * @since	1.6	 */	public function getModel($name = 'Member', $prefix = 'jSchuetzeModel', 							 $config = array('ignore_request' => true))	{		$model = parent::getModel($name, $prefix, $config);		return $model;	}	    public function setScet_mail_notification_State($state)    {        $this->setRedirect( 'index.php?option=com_jschuetze&view=members' );        $this->model = $this->getModel();        $cid   = JRequest::getVar( 'cid', array(), 'post', 'array' );                $rows = $this->model->setScetmailNotificationState($state, $cid);                JFactory::getApplication()->enqueueMessage(sprintf(JText::_('COM_JSCHUETZE_ROWS_AFFECTED'), $rows ) );    }    public function scet_mail_notificationpublish()    {        $this->setScet_mail_Notification_State('1');    }    public function scet_mail_notificationunpublish()    {        $this->setScet_mail_Notification_State('0');    }    }